    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try{
            String st=jPasswordField3.getText();
            String st2=jPasswordField4.getText();
            Connection cn=DriverManager.getConnection("jdbc:mysql://localhost/db_chat","root","");
             PreparedStatement pst = cn.prepareStatement("insert into usuarios values (?,?,?,?)") ;
             if(st.equals(st2)){
                pst.setString(1,"0");
                pst.setString(2,jTextField1.getText().trim());
                pst.setString(4,jTextField2.getText().trim());
                pst.setString(3,jPasswordField3.getText().trim());
                jLabel1.setText("*El registro fue un exito.");
                jLabel1.setForeground(Color.blue);
             }else{
                
                jLabel1.setText("*Las contraseñas deben ser identicas.");
                jLabel1.setForeground(Color.red);
           
             }pst.executeUpdate();
             
             
             jTextField1.setText("");
             jTextField2.setText("");
             jPasswordField3.setText("");
             jPasswordField4.setText("");
        }catch (Exception e){
            System.err.print("Error +"+ e);
       }
    }//GEN-LAST:event_jButton1ActionPerformed

