    private void jCheckBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCheckBox1ActionPerformed
        jTextArea1.setVisible(true);
        jTextArea1.setEnabled(false);
        JOptionPane.showMessageDialog(null,"Usted esta aceptando los terminos de uso.");
        jTextArea1.setText("Todos los datos y documentos ingresados en este formulario serán guardados en una base de datos de seguridad limitada y código libre.\n" +
"Los textos del chat no serán guardados, una vez borrados no podrán recuperarse.\n" +
"Usted está dando consentimiento y aceptando los términos de registro.");
    }//GEN-LAST:event_jCheckBox1ActionPerformed

